function f_[ElementId](config) {
  this.Config = config;

  this.Handle = internalHandle;

  function GetData(){

    GetDashboardData(config.DataId,function(json){

      // 实例化 图表辅助工具
      var helper = new HighchartsHelper();
      // 获取 系列
      var seriesData = helper.getPieSeriesData(json.Table, "[SerieColumnName]", "[ValueColumnName]");

      // 获取图表 tilte
      var title = "[Title]";
      // 获取图表子标题
      var subtitle = "[subTitle]";

      $("#mainDiv_" + config.ElementId + " #container").highcharts({
        credits: {
        	enabled: false
        },
        title: {
            text: title,
        },
        subtitle: {
            text: subtitle,
        },
        plotOptions: {
            pie: {
                allowPointSelect: true,
                cursor: 'pointer',
                dataLabels: {
                    enabled: true,
                    color: '#000000',
                    connectorColor: '#000000',
                    format: '<b>{point.name}</b>: {point.percentage:.1f} %'
                }
            }
        },
        series: [{
            type: 'pie',
            data: seriesData
        }]
    });
    });
  }

  function internalHandle() {
    if(this.Config.IsAutoRefresh)
    {
      GetData();
      setInterval(function () { GetData() }, this.Config.Interval);
    }
    else
    {
      GetData();
    }
  }
}

var fun_[ElementId] = 
  new f_[ElementId]({"ElementId" : "[ElementId]","DataId": "[DataId]","IsAutoRefresh": [IsAutoRefresh],"Interval": [Interval]}).Handle();

