function f_[ElementId](config) {
    this.Config = config;
    this.Handle = internalHandle;

    var _config = this.Config;

    function GetData() {
        GetDashboardData(config.DataId, function (json) {
            //********** Your Code Begin **********

            if (json.Table == null)
                return;

            var $tab = $("#mainDiv_" + config.ElementId + " > table");

            $tab.html(GenTableInnerHtml(json.Table, [CellHandleFunc]));

            if('[FontSize]' != '' ) { $tab.css("font-size","[FontSize]px"); }

            if ([Bordered]) { $tab.addClass("table-bordered"); }
            if ([MouseHover]) { $tab.addClass("table-hover"); }
            if ([Condensed]) { $tab.addClass("table-condensed"); }
          	if ([Striped]) { $tab.addClass("table-striped"); }

            var headtr = $tab.find("thead tr"); 
            if ([ShowTableHead]) { if ('[HeadStatus]' != '') { $(headtr[0]).addClass('[HeadStatus]'); } } 
          	else { $(headtr[0]).css("display", "none"); }

            var pageIndex = 0;
            var pageSize = [PageSize];
            var pageInterval = [PageInterval];
            var pageCount = json.Table.length / pageSize;

            if(pageSize < json.Table.length){
                if(_config.timer != null)
                  clearTimeout(_config.timer);

                var trs = $tab.find("tbody tr");

                var handleTable = function () {
                    if (pageIndex >= pageCount)
                        pageIndex = 0;

                    for (var i = 0; i < json.Table.length; i++) {
                        if (i >= pageIndex * pageSize && i < (pageIndex + 1) * pageSize)
                            $(trs[i]).css("display", "");
                        else
                            $(trs[i]).css("display", "none");
                    }
                    pageIndex++;
                }

              	handleTable();
              	_config.timer = setInterval(handleTable, pageInterval);
            }

            // ********** End **********
        });
    }

    function internalHandle() {
        if (this.Config.IsAutoRefresh) {
            GetData();
            setInterval(function () { GetData() }, this.Config.Interval);
        }
        else {
            GetData();
        }
    }
}

var fun_[ElementId] =
  new f_[ElementId]({ "ElementId": "[ElementId]", "DataId": "[DataId]", "IsAutoRefresh": [IsAutoRefresh], "Interval": [Interval] }).Handle();

