function f_[ElementId](config) {
  this.Config = config;

  this.Handle = internalHandle;

  function GetData(){
    GetDashboardData(config.DataId,function(json){
      // 实例化 图表辅助工具
      var helper = new HighchartsHelper();
	  // 获取 X 轴数据
      var xAxisCategories = helper.getxAxisCategories(json.Table, "[XAxisCategory]");
      // 获取 系列
      var series = helper.getSeries(json.Table, xAxisCategories, "[XAxisCategory]", "[SerieColumnName]", "[ValueColumnName]");

      // 为每个柱状图 增加 数值显示
      for(s in series)
      {
      	series[s].dataLabels = {
                enabled: true,
                rotation: 0,
                color: '#FFFFFF',
                align: 'right',
                x: 0,
                y: 0,
                style: {
                    fontSize: '13px',
                    fontFamily: 'Verdana, sans-serif',
                    textShadow: '0 0 3px black'
                }
            };
      }

      // 获取图表 tilte
      var title = "[Title]";
      // 获取图表子标题
      var subtitle = "[SubTitle]";
      // 获取图表 X 轴的标题
      var xAxisTile = "[XAxisTitle]";
      // 获取图表 Y 轴的标题
      var yAxisTile = "[YAxisTitle]";

      $("#mainDiv_" + config.ElementId + " #container").highcharts({
        credits: {
        	enabled: false
        },
        chart: {
            type: 'bar'
        },
        title: {
            text: title
        },
        subtitle: {
            text: subtitle
        },
        xAxis: {
        	categories: xAxisCategories,
          	title: {
            	text: xAxisTile
            }
        },
        yAxis: {
            min: 0,
            title: {
                text: yAxisTile,
                align: 'high'
            },
            labels: {
                overflow: 'justify'
            }
        },
        plotOptions: {
            bar: {
                dataLabels: {
                    enabled: true
                }
            }
        },
        series: series
      });
    });
  }

  function internalHandle() {
    if(this.Config.IsAutoRefresh)
    {
      GetData();
      setInterval(function () { GetData() }, this.Config.Interval);
    }
    else
    {
      GetData();
    }
  }
}

var fun_[ElementId] = 
  new f_[ElementId]({"ElementId" : "[ElementId]","DataId": "[DataId]","IsAutoRefresh": [IsAutoRefresh],"Interval": [Interval]}).Handle();
