//Siemens Dashboard 自动生成代码，请勿擅自改动。仅在指示区域进行编辑

//自动生成的 函数（f + 元素ID）、保证全局唯一
//参数 config 说明。
//config 为json对象.包含以下可访问属性 [ElementId,DataId,IsAutoRefresh,Interval]

function f_[ElementId](config) {
  // 方法属性 Config 赋值（可在之后使用此方法时再次访问config属性）
  this.Config = config;

  // 方法赋值
  this.Handle = internalHandle;

  // 获取数据
  function GetData(){
    // 内置函数.2个参数。【dataId:数据ID,success:查询成功后回调函数】
    // success回调函数参数[json]为DataSet序列化后的json对象.取第一个表的表达式为 json.Table,取第二个表的表达式为 json.Table1
    // 依次类推（可使用sql语句、或存储过程返回包含多个数据表的数据集）
    // 例如 生成表格
    // $("#mainDiv_" + config.ElementId + " > table").html(GenTableInnerHtml(json.Table));
    GetDashboardData(config.DataId,function(json){
      //********** 自定义编码 Begin **********

      if (json.Table == null)
                return;

      var $progress = $("#mainDiv_" + config.ElementId + " #prog");

      $progress.attr("aria-valuenow",json.Table[0].[ProgressColumnName]);
      $progress.css("width",json.Table[0].[ProgressColumnName] + "%");

      $progress.html(json.Table[0].[DesColumnName]);

      if('[FontSize]' != '' ) { $progress.css("font-size","[FontSize]px"); }

      // ********** 自定义编码 End **********
    });
  }

  // 内部处理方法
  function internalHandle() {
    // 是否自动刷新
    if(this.Config.IsAutoRefresh)
    {
      // 自动刷新
      GetData();
      setInterval(function () { GetData() }, this.Config.Interval);
    }
    else
    {
      //无需自动刷新
      GetData();
    }
  }
}

// 实例化处理方法 并调用其 Hanlde 方法
var fun_[ElementId] = 
  new f_[ElementId]({"ElementId" : "[ElementId]","DataId": "[DataId]","IsAutoRefresh": [IsAutoRefresh],"Interval": [Interval]}).Handle();

