(function () { 
  if('[FontSize]' != '')
    $("#mainDiv_[ElementId] #showDate").css("font-size","[FontSize]px");

  switch("[TimeFormat]")
  {
    case "YYYYMMDDWEEKDAY":
      $("#mainDiv_[ElementId] #showDate").html($.getYYYYMMDDWEEKDAY()); 
      setInterval(
        function () { 
          $("#mainDiv_[ElementId] #showDate").html($.getYYYYMMDDWEEKDAY()); 
        },
        24 * 60 * 60 * 1000); 
      break;
    case "YYYYMMDD":
      $("#mainDiv_[ElementId] #showDate").html($.getYYYYMMDD()); 
      setInterval(
        function () { 
          $("#mainDiv_[ElementId] #showDate").html($.getYYYYMMDD()); 
        },
        24 * 60 * 60 * 1000);
      break;
    case "HHMMSS":
      $("#mainDiv_[ElementId] #showDate").html($.getHHMMSS()); 
      setInterval(
        function () { 
          $("#mainDiv_[ElementId] #showDate").html($.getHHMMSS()); 
        },
        1000); 
      break;
    case "HHMM":
      $("#mainDiv_[ElementId] #showDate").html($.getHHMM()); 
      setInterval(
        function () { 
          $("#mainDiv_[ElementId] #showDate").html($.getHHMM()); 
        },
        60 * 1000); 
      break;
    case "YYYYMMDDHHMMSS":
      $("#mainDiv_[ElementId] #showDate").html($.getYYYYMMDDHHMMSS()); 
      setInterval(
        function () { 
          $("#mainDiv_[ElementId] #showDate").html($.getYYYYMMDDHHMMSS()); 
        },
        1000); 
      break;
    default:
      break;
  }
}
).call(this);

